mapper.select:
select userid, username, email, interest_sido, interest_gugun, interest_dong
from user
where userid = #{userid} and userpwd = #{userpwd}

select userid, userpwd, username, email, interest_sido, interest_gugun, interest_dong
from user
where userid = #{userid}

mapper.update:
update user
set userpwd = #{userpwd}, username = #{username}, email = #{email}, interest_sido = #{interest_sido}, interest_gugun = #{interest_gugun}, interest_dong = #{interest_dong}
where userid = #{userid}
